    posts: []
  };
  componentDidMount() {
    axios
      .get(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=4b18e39dcff44fe999709f920eb3cda7"
      )
      .then(res => {
        console.log(res);
        this.setState({
