import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

import CardMedia from '@material-ui/core/CardMedia';
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 151,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
  playIcon: {
    height: 38,
    width: 38,
  },
}));
class Home extends Component {
  state = {
    posts: []
  };
  componentDidMount() {
    axios
      .get(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=4b18e39dcff44fe999709f920eb3cda7"
      )
      .then(res => {
        this.setState({
          posts: res.data.articles
        });
        console.log(this.state.posts);
      });
      
      
  }
  render() {
    const classes = useStyles();
    const { posts } = this.state;
    const postList = posts.length ? (
      posts.map(post => {
        return (
          <div className="post card" key={post.source.id}>
            <div className="card-content">
              <a href={post.url}>
                <span className="card-title">{post.title}</span>
              </a>
              <img src={post.urlToImage}  />
              <CardMedia
                className={classes.cover}
                image="post.urlToImage"
              />
              <div>{post.urlToImage}</div>
              <p>{post.description}</p>
            </div>
          </div>
        );
      })
    ) : (
      <div className="center">No posts yet</div>
    );
    return (
      <div className="container">
        <h4 className="center">Top headlines of the day!</h4>
        {postList}
      </div>
    );
  }
}

export default Home;
